import "./styles.css"
import Titulos from "../infos/Titulos/Titulos.js";
import axios from "axios";
import { useState, useEffect } from "react";
import Dia from "./Day/Day";
import { useParams } from "react-router-dom";
import Loading from "../infos/Loading/Loading";
import SessionPage from "../SessionPage/SessionPage";

export default function Sessao() {
    const { filmId } = useParams();

    const [movie, setMovie] = useState(null);
    useEffect(() => {
        axios.get(`https://mock-api.driven.com.br/api/v4/cineflex/movies/${filmId}/showtimes`)
        .then((response) => setMovie(response.data))
        .catch((error) => alert(error))
    }, [])

    if (movie === null) {
        return (
            <Loading />
        )
    }

    return (
        <article className="sessionsPage" >
            <Titulos text="Selecione o horÃ¡rio" />
            <section className="day-box" >
            {movie.days.map((day) => (
                    <Dia
                        weekday={day.weekday}
                        date={day.date}
                        showtimes={day.showtimes}
                        key={day.id}
                    />
                ))}
            </section>
        </article>
    )
} 
